import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  aiAnswer: string = '';
  audio: string = '';
  fuelData: any[] = [];
  productionData: any[] = [];
  kpiData: any = {};                // Pass this to <app-kpis [kpiData]="kpiData">
  guidancePoints: string[] = [];    // Pass this to <app-guidance [managerGuidance]="guidancePoints">

  handleQueryResult(result: any) {
    this.aiAnswer = result.answer;
    this.audio = result.audio?.audio_base64 || '';
    this.fuelData = result.data?.fuel_chart || [];
    this.productionData = result.data?.production_chart || [];
    this.kpiData = result.data?.kpi_cards || {};
    this.guidancePoints = result.data?.manager_guidance || [];
  }
}
<div class="dashboard">
  <h1>⛏️ Mining Intelligence Dashboard</h1>

  <!-- Chat -->
  <app-chat (queryResponse)="handleQueryResult($event)"></app-chat>

  <!-- KPI Section -->
  <app-kpis [kpiData]="kpiData"></app-kpis>

  <!-- Charts Section -->
  <app-charts 
    [fuelChartData]="fuelData" 
    [productionChartData]="productionData">
  </app-charts>

  <!-- Manager Guidance -->
  <app-guidance [managerGuidance]="guidancePoints"></app-guidance>
</div>
