import { Component, Input, OnInit } from '@angular/core';
import { ApiService } from '../../services/api.service';
import { AudioService } from '../../services/audio.service';
import { ChatMessage } from '../../models/interfaces';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {
  @Input() language: string = 'en';
  messages: ChatMessage[] = [];
  userInput: string = '';
  isLoading: boolean = false;
  includeAudio: boolean = true;

  constructor(private apiService: ApiService, private audioService: AudioService) {}

  ngOnInit(): void {
    this.messages.push({
      role: 'assistant',
      content: 'Hello! I\'m your Mining Intelligence Assistant. Ask me anything about incidents, equipment, production, or get management guidance.',
      timestamp: new Date()
    });
  }

  sendMessage(): void {
    if (!this.userInput.trim() || this.isLoading) return;

    const userMessage: ChatMessage = {
      role: 'user',
      content: this.userInput,
      timestamp: new Date()
    };
    this.messages.push(userMessage);
    const query = this.userInput;
    this.userInput = '';
    this.isLoading = true;

    this.apiService.sendMessage(query, this.language, this.includeAudio).subscribe({
      next: (response: any) => {
        const assistantMessage: ChatMessage = {
          role: 'assistant',
          content: response.answer,
          kpis: response.kpis,
          charts: response.charts,
          audio: response.audio,
          timestamp: new Date(response.timestamp)
        };
        this.messages.push(assistantMessage);

        if (this.includeAudio && response.audio?.success && response.audio.audio_base64) {
          this.audioService.playAudio(response.audio.audio_base64);
        }
        this.isLoading = false;
        this.scrollToBottom();
      },
      error: (err) => {
        this.messages.push({
          role: 'assistant',
          content: 'Sorry, an error occurred. Try again.',
          timestamp: new Date()
        });
        this.isLoading = false;
        console.error(err);
      }
    });
  }

  /*handleEnter(event: KeyboardEvent): void {
    event.preventDefault();
    this.sendMessage();
  }*/
 /*handleEnter(event: KeyboardEvent) {
  if (event.key === 'Enter' && !event.shiftKey) {
    event.preventDefault();
    this.sendMessage();
  }
}*/
handleEnterEvent(event: Event): void {
  const keyboardEvent = event as KeyboardEvent; // cast safely here
  if (!keyboardEvent.shiftKey) {
    keyboardEvent.preventDefault();
    this.sendMessage();
  }
}



  playAudio(message: ChatMessage): void {
    if (message.audio?.success && message.audio.audio_base64) {
      this.audioService.playAudio(message.audio.audio_base64);
    }
  }

  stopAudio(): void { this.audioService.stopAudio(); }

  clearChat(): void {
    this.messages = [];
    this.ngOnInit();
  }

  scrollToBottom(): void {
    setTimeout(() => {
      const chatContainer = document.querySelector('.chat-messages');
      if (chatContainer) chatContainer.scrollTop = chatContainer.scrollHeight;
    }, 100);
  }
}
<div class="chat-container">
  <div class="chat-header">
    <h2>üí¨ AI Assistant</h2>
    <div class="chat-actions">
      <label class="audio-toggle">
        <input type="checkbox" [(ngModel)]="includeAudio">
        üîä Audio Responses
      </label>
      <button class="clear-btn" (click)="clearChat()">üóëÔ∏è Clear</button>
    </div>
  </div>

  <div class="chat-messages">
    <div *ngFor="let message of messages" 
         [class.user-message]="message.role === 'user'"
         [class.assistant-message]="message.role === 'assistant'"
         class="message">
      
      <div class="message-avatar">
        {{ message.role === 'user' ? 'üë§' : 'ü§ñ' }}
      </div>
      
      <div class="message-content">
        <div class="message-text">{{ message.content }}</div>

        <!-- KPIs -->
        <div *ngIf="message.kpis" class="kpi-cards">
          <div>Total Incidents: {{ message.kpis.total_incidents }}</div>
          <div>Critical Alerts: {{ message.kpis.critical_alerts }}</div>
          <div>Avg Efficiency: {{ message.kpis.avg_efficiency }}%</div>
          <div>Monthly Production: {{ message.kpis.monthly_production }}</div>
        </div>

        <!-- Charts -->
        <div *ngIf="message.charts">
          <div *ngFor="let chart of message.charts">
            <h4>{{ chart.title }}</h4>
            <app-charts [type]="chart.type" [data]="chart.data"></app-charts>
          </div>
        </div>

        <div class="message-footer">
          <span class="message-time">{{ message.timestamp | date:'short' }}</span>
          <button *ngIf="message.audio?.success" (click)="playAudio(message)">üîä Play Audio</button>
        </div>
      </div>
    </div>

    <div *ngIf="isLoading" class="loading-indicator">
      <div class="typing-animation"><span></span><span></span><span></span></div>
      <span>AI is thinking...</span>
    </div>
  </div>

 <div class="chat-input-container">
  <textarea
  [(ngModel)]="userInput"
  (keydown.enter)="handleEnterEvent($event)"
