@Component({
  selector: 'app-guidance',
  templateUrl: './guidance.component.html',
  styleUrls: ['./guidance.component.css']
})
export class GuidanceComponent {
  @Input() managerGuidance: string[] = [];
  @Input() language: string = 'en'; // added for template binding
}
