// src/app/app.component.ts
import { Component, OnInit } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Mining Intelligence System';
  isHealthy = false;
  selectedLanguage = 'en';
  languages: any = {};

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.checkHealth();
    this.loadLanguages();
  }

  checkHealth(): void {
    this.apiService.healthCheck().subscribe({
      next: (response) => {
        this.isHealthy = response.status === 'healthy';
        console.log('‚úÖ Backend is healthy:', response);
      },
      error: (error) => {
        this.isHealthy = false;
        console.error('‚ùå Backend health check failed:', error);
      }
    });
  }

  loadLanguages(): void {
    this.apiService.getLanguages().subscribe({
      next: (response) => {
        if (response.success) {
          this.languages = response.languages;
        }
      },
      error: (error) => console.error('Error loading languages:', error)
    });
  }

  onLanguageChange(event: any): void {
    this.selectedLanguage = event.target.value;
  }
}
