import { Component } from '@angular/core';
import { CommonModule, DatePipe } from '@angular/common';  // <-- For *ngIf, *ngFor, date pipe
import { FormsModule } from '@angular/forms';              // <-- For [(ngModel)]

interface ChatMessage {
  sender: 'user' | 'ai';
  text: string;
  timestamp: Date;
}

interface Alert {
  equipment_name: string;
  message: string;
  severity?: 'urgent' | 'pending' | 'normal';
  days_until?: number;
}

interface Equipment {
  id: number;
  name: string;
  type: string;
  status?: string;
}

interface CarbonFootprint {
  co2_emissions_tons?: number;
  sdg_13_status?: string;
}

@Component({
  selector: 'app-root',          // <-- mark it as standalone
 // imports: [CommonModule, FormsModule, DatePipe],  // <-- important!
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  messages: ChatMessage[] = [
    { sender: 'ai', text: 'Welcome to AI Mining Operations Co-Pilot! Ask me anything about fuel, maintenance, production, or emissions.', timestamp: new Date() }
  ];
  userInput = '';
  isLoading = false;

  alerts: Alert[] = [
    { equipment_name: 'Excavator 1', message: 'Maintenance due soon', severity: 'pending', days_until: 5 },
    { equipment_name: 'Crusher A', message: 'Filter replacement required', severity: 'urgent', days_until: 2 }
  ];

  equipmentData: Equipment[] = [
    { id: 1, name: 'Excavator 1', type: 'Excavator', status: 'Active' },
    { id: 2, name: 'Drill B', type: 'Drill', status: 'Active' }
  ];

  carbonFootprint: CarbonFootprint = {
    co2_emissions_tons: 120,
    sdg_13_status: 'Moderate'
  };

  uploadTypes = [
    { label: 'Fuel Consumption', key: 'fuel' },
    { label: 'Production Efficiency', key: 'production' },
    { label: 'Equipment Details', key: 'equipment' },
    { label: 'Maintenance Records', key: 'maintenance' },
  ];

  selectedFiles: { [key: string]: File } = {};
  showUpload = false;

  toggleUpload() {
    this.showUpload = !this.showUpload;
  }

  sendMessage() {
    if (!this.userInput.trim()) return;
    const msg: ChatMessage = { sender: 'user', text: this.userInput, timestamp: new Date() };
    this.messages.push(msg);
    this.isLoading = true;
    this.userInput = '';

    setTimeout(() => {
      this.messages.push({
        sender: 'ai',
        text: 'This is a sample AI response. Your uploaded mining data will help me provide insights soon!',
        timestamp: new Date()
      });
      this.isLoading = false;
    }, 1000);
  }

  onFileSelected(event: any, type: string) {
    const file = event.target.files[0];
    if (file) this.selectedFiles[type] = file;
  }

  uploadFile(type: string) {
    const file = this.selectedFiles[type];
    if (!file) return alert('Please select a file to upload.');
    alert(`File "${file.name}" uploaded for ${type}`);
  }

 getAlertClass(severity?: string) {
  switch(severity) {
    case 'urgent': return 'alert-urgent';
    case 'pending': return 'alert-pending';
    default: return 'alert-normal';
  }
}
}
